rules.contents:
if (STATE[A.x, A.y] == Off)
{
	STATE[A.x, A.y] = On;
}
else
{
	STATE[A.x, A.y] = Off;
}

if (STATE[A.x, A.y + 1] == On)
{
	STATE[A.x, A.y + 1] = Off;
}
else
{
	STATE[A.x, A.y + 1] = On;
}

if (STATE[A.x + 1, A.y] == On)
{
	STATE[A.x + 1, A.y] = Off;
}
else
{
	STATE[A.x + 1, A.y] = On;
}

if (STATE[A.x - 1, A.y] == On)
{
	STATE[A.x - 1, A.y] = Off;
}
else
{
	STATE[A.x - 1, A.y] = On;
}

if (STATE[A.x, A.y - 1] == On)
{
	STATE[A.x, A.y - 1] = Off;
}
else
{
	STATE[A.x, A.y - 1] = On;
}
