rules.contents:
if ((STATE[A.x + 1, A.y] == Blue) OR (STATE[A.x + 1, A.y] == Red))
{
	VALID[A.x + 1, A.y] = true;
}

if ((STATE[A.x + 2, A.y] == Blue) OR (STATE[A.x + 2, A.y] == Red))
{
	VALID[A.x + 2, A.y] = true;
}

if ((STATE[A.x - 1, A.y] == Blue) OR (STATE[A.x - 1, A.y] == Red))
{
	VALID[A.x - 1, A.y] = true;
}

if ((STATE[A.x - 2, A.y] == Blue) OR (STATE[A.x - 2, A.y] == Red))
{
	VALID[A.x - 2, A.y] = true;
}

if ((STATE[A.x, A.y + 1] == Blue) OR (STATE[A.x, A.y + 1] == Red))
{
	VALID[A.x, A.y + 1] = true;
}

if ((STATE[A.x, A.y + 2] == Blue) OR (STATE[A.x, A.y + 2] == Red))
{
	VALID[A.x, A.y + 2] = true;
}

if ((STATE[A.x, A.y - 1] == Blue) OR (STATE[A.x, A.y - 1] == Red))
{
	VALID[A.x, A.y - 1] = true;
}

if ((STATE[A.x, A.y - 2] == Blue) OR (STATE[A.x, A.y - 2] == Red))
{
	VALID[A.x, A.y - 2] = true;
}
